import React, { useState } from 'react';
import { auth, firestore } from '../firebase/firebase';

const CompleteProfile = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    currentWeight: '',
    goalWeight: '',
    gender: '',
    birthday: '',
    height: '',
    activityLevel: '',
    dietPreferences: '',
    foodAllergies: '',
    goalIntakes: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { user } = await auth.createUserWithEmailAndPassword(formData.email, formData.password);
      await firestore.collection('users').doc(user.uid).set({
        firstName: formData.firstName,
        lastName: formData.lastName,
        currentWeight: formData.currentWeight,
        goalWeight: formData.goalWeight,
        gender: formData.gender,
        birthday: formData.birthday,
        height: formData.height,
        activityLevel: formData.activityLevel,
        dietPreferences: formData.dietPreferences,
        foodAllergies: formData.foodAllergies,
        goalIntakes: formData.goalIntakes,
      });
      console.log('User account created');
      // Redirect or show success message
    } catch (error) {
      console.error('Error creating account:', error);
      // Handle error
    }
  };

  return (
    <div>
      <h2>Complete Your Profile</h2>
      <h4>Not Required but more information the more accurately our website can help</h4>
      <form onSubmit={handleSubmit}>
        {/* Add input fields for each piece of information */}
        <input type="text" name="firstName" value={formData.firstName} onChange={handleChange} placeholder="First Name" required />
        <input type="text" name="lastName" value={formData.lastName} onChange={handleChange} placeholder="Last Name" />
        <input type="text" name="currentWeight" value={formData.currentWeight} onChange={handleChange} placeholder="Current Weighr" />
        {/* Add other input fields similarly */}
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default CompleteProfile;
